import { createElement } from 'react';
import { useArticle } from "../../../hooks/use-shopify-data";
import { elementAttr } from "../../../utils/template-helpers/element-attr";
import './styles';
export const ArticleTitle = ({ element }) => {
    const { settings } = element;
    const linkEnabled = settings.linkEnabled === 'yes';
    const titleClass = `xb-heading xb-${settings.htmlTag}`;
    const classLink = linkEnabled ? 'xb-link' : '';
    const linkTag = linkEnabled ? 'a' : 'div';
    const article = useArticle(element.id);
    if (!article) {
        return null;
    }
    return createElement(settings.htmlTag, {
        ...elementAttr(element, {
            classNames: ['xb-article-title', titleClass, settings.static?.class],
        }),
        children: createElement(linkTag, {
            className: classLink,
            href: `/blogs/${article.blog?.handle}/${article?.handle}`,
            dangerouslySetInnerHTML: { __html: article?.title },
        }),
    });
};
