export declare const PRODUCT_QUERY: "#graphql\n  #graphql\n  fragment Media on Media {\n    __typename\n    mediaContentType\n    alt\n    previewImage {\n      url\n    }\n    ... on MediaImage {\n      id\n      image {\n        id\n        url\n        width\n        height\n      }\n    }\n    ... on Video {\n      id\n      sources {\n        mimeType\n        url\n      }\n    }\n    ... on Model3d {\n      id\n      presentation {\n        asJson(format: MODEL_VIEWER)\n        id\n      }\n      sources {\n        filesize\n        format\n        mimeType\n        url\n      }\n    }\n    ... on ExternalVideo {\n      id\n      embedUrl\n      host\n    }\n  }\n\n  #graphql\n  fragment ProductVariantFragment on ProductVariant {\n    id\n    availableForSale\n    quantityAvailable\n    selectedOptions {\n      name\n      value\n    }\n    image {\n      id\n      url\n      altText\n      width\n      height\n    }\n    price {\n      amount\n      currencyCode\n    }\n    compareAtPrice {\n      amount\n      currencyCode\n    }\n    sku\n    title\n    unitPrice {\n      amount\n      currencyCode\n    }\n    quantityAvailable\n    product {\n      title\n      handle\n    }\n    storeAvailability(first: 100) {\n      nodes {\n        available\n        pickUpTime\n        quantityAvailable\n        location {\n          name\n          address {\n            address1\n            address2\n            city\n            country\n            countryCode\n            formatted\n            latitude\n            longitude\n            phone\n            province\n            provinceCode\n            zip\n          }\n        }\n      }\n    }\n  }\n\n  query Product(\n    $country: CountryCode\n    $language: LanguageCode\n    $handle: String!\n    $selectedOptions: [SelectedOptionInput!]!\n  ) @inContext(country: $country, language: $language) {\n    product(handle: $handle) {\n      id\n      title\n      vendor\n      handle\n      descriptionHtml\n      description\n      encodedVariantExistence\n      encodedVariantAvailability\n      options {\n        name\n        optionValues {\n          name\n          firstSelectableVariant {\n          ...ProductVariantFragment\n          }\n          swatch {\n            color\n            image {\n              previewImage {\n                url\n              }\n            }\n          }\n        }\n      }\n      selectedOrFirstAvailableVariant(selectedOptions: $selectedOptions, ignoreUnknownOptions: true, caseInsensitiveMatch: true) {\n        ...ProductVariantFragment\n      }\n      adjacentVariants (selectedOptions: $selectedOptions) {\n       ...ProductVariantFragment\n      }\n      featuredImage {\n        id\n        altText\n        url\n        width\n        height\n      }\n      media(first: 100) {\n        nodes {\n          ...Media\n        }\n      }\n      variants(first: 100) {\n        nodes {\n          ...ProductVariantFragment\n        }\n      }\n      seo {\n        description\n        title\n      }\n      updatedAt\n      totalInventory\n      collections(first: 1) {\n        edges {\n          node {\n            handle\n            title\n          }\n        }\n      }\n    }\n  }\n";
