import { PRODUCT_ITEM_FRAGMENT } from './fragments';
// @see: https://shopify.dev/docs/api/storefront/2025-01/queries/productRecommendations
export const PRODUCT_RECOMMEDATION_QUERY = `#graphql
  ${PRODUCT_ITEM_FRAGMENT}
  query productRecommendations(
    $productHandle: String
    $country: CountryCode
    $language: LanguageCode
  ) @inContext(country: $country, language: $language) {
    productRecommendations(productHandle: $productHandle) {
      ...ProductItem
    }
  }
`;
