import { PRODUCT_ITEM_FRAGMENT } from './fragments';
// @see: https://shopify.dev/docs/api/storefront/2025-01/queries/products
export const PRODUCTS_QUERY = `#graphql
  ${PRODUCT_ITEM_FRAGMENT}
  query Products(
    $country: CountryCode
    $endCursor: String
    $first: Int
    $language: LanguageCode
    $last: Int
    $startCursor: String
  ) @inContext(country: $country, language: $language) {
    products(
      first: $first,
      last: $last,
      before: $startCursor,
      after: $endCursor
    ) {
      edges {
        node {
          ...ProductItem
        }
      }
    }
  }
`;
