// @see: https://shopify.dev/docs/api/storefront/2025-01/queries/shop
export const SHOP_QUERY = `#graphql
  query Shop {
    shop {
      name
      paymentSettings {
        acceptedCardBrands
        cardVaultUrl
        countryCode
        currencyCode
        enabledPresentmentCurrencies
        supportedDigitalWallets
      }
      primaryDomain {
        url
      }
    }
  }
`;
