export function convertObjectToCSSProperties(obj, setImportant = false) {
    return Object.entries(obj)
        .map(([key, value]) => {
        // Append `!important` if setImportant is true
        const importantSuffix = setImportant ? ' !important' : '';
        return value ? `${key}: ${value}${importantSuffix};` : '';
    })
        .filter(Boolean)
        .join('\n');
}
