export function actionAttr(action, useXbHref = false) {
    if (!action) {
        return {};
    }
    const hrefAttr = useXbHref ? 'xb-href' : 'href';
    const targetAttr = useXbHref ? 'xb-target' : 'target';
    switch (action.actionType) {
        case 'url':
            if (!action.url) {
                return {};
            }
            if (action.openNewTab) {
                return {
                    [hrefAttr]: action.url,
                    [targetAttr]: '_blank',
                };
            }
            return {
                [hrefAttr]: action.url,
            };
        case 'email':
            return {
                [hrefAttr]: `mailto:${action.email}?subject=${action.emailSubject ?? ''}&body=${action.emailBody ?? ''}`,
            };
        case 'phone':
            return {
                [hrefAttr]: `tel:${action.phone}`,
            };
        case 'scroll':
            return {
                'xo-scroll-to': 'true',
                [hrefAttr]: action?.scrollTo ?? '',
                'xo-offset': action?.topOffset?.value ?? 0,
            };
        default:
            return {};
    }
}
