export const getCssVariableParallax = (styles) => {
    if (!styles?.normal?.background?.['background-image-parallax']) {
        return '';
    }
    const parallax = styles?.normal?.background;
    const bgRepeatDesktop = parallax?.['background-repeat']?.desktop || 'repeat';
    const bgRepeatTablet = parallax?.['background-repeat']?.tablet || bgRepeatDesktop;
    const bgRepeatMobile = parallax?.['background-repeat']?.mobile || bgRepeatTablet || bgRepeatDesktop;
    const bgPositionDesktop = parallax?.['background-position']?.desktop
        ? `${parallax?.['background-position']?.desktop.x}% ${parallax?.['background-position']?.desktop.y}%`
        : 'center';
    const bgPositionTablet = parallax?.['background-position']?.tablet
        ? `${parallax?.['background-position']?.tablet.x}% ${parallax?.['background-position']?.tablet.y}%`
        : bgPositionDesktop;
    const bgPositionMobile = parallax?.['background-position']?.mobile
        ? `${parallax?.['background-position']?.mobile.x}% ${parallax?.['background-position']?.mobile.y}%`
        : bgPositionTablet || bgPositionDesktop;
    const bgSizeDesktop = parallax?.['background-size']?.desktop || 'cover';
    const bgSizeTablet = parallax?.['background-size']?.tablet || bgSizeDesktop;
    const bgSizeMobile = parallax?.['background-size']?.mobile || bgSizeTablet || bgSizeDesktop;
    const parallaxDesktop = parallax?.['background-image']?.desktop?.url;
    const parallaxTablet = parallax?.['background-image']?.tablet?.url || parallaxDesktop;
    const parallaxMobile = parallax?.['background-image']?.mobile?.url || parallaxTablet || parallaxDesktop;
    return `--bg-repeat-desktop: ${bgRepeatDesktop};--bg-repeat-tablet: ${bgRepeatTablet};--bg-repeat-mobile: ${bgRepeatMobile};--bg-position-desktop: ${bgPositionDesktop};--bg-position-tablet: ${bgPositionTablet};--bg-position-mobile: ${bgPositionMobile};--bg-size-desktop: ${bgSizeDesktop};--bg-size-tablet: ${bgSizeTablet};--bg-size-mobile: ${bgSizeMobile};--parallax-desktop: url(${parallaxDesktop});--parallax-tablet: url(${parallaxTablet});--parallax-mobile: url(${parallaxMobile});`;
};
