export function renderAction(action, content = '', attrs = '', designMode = false) {
    switch (action.actionType) {
        case 'url':
            if (!action.url) {
                return `<div ${attrs}>${content}</div>`;
            }
            if (action.openNewTab) {
                return `<a href="${action.url}" target="_blank" ${attrs}>${content}</a>`;
            }
            return `<a href="${action.url}" ${attrs}>${content}</a>`;
        case 'email':
            return `<a href="mailto:${action.email}?subject=${action.emailSubject ?? ''}&body=${action.emailBody ?? ''}" ${attrs}>${content}</a>`;
        case 'phone':
            return `<a href="tel:${action.phone}" ${attrs}>${content}</a>`;
        case 'scroll':
            if (designMode) {
                return `<a href="${action.scrollTo}" xo-offset="${action.topOffset.value}" ${attrs}>${content}</a>`;
            }
            return `<a is="xo-scroll-to" href="${action.scrollTo}" xo-offset="${action.topOffset?.value ?? 0}" ${attrs}>${content}</a>`;
        default:
            return content;
    }
}
